<?php

declare(strict_types=1);

namespace tests\api;

use ApiTester;
use Codeception\Util\HttpCode;
use Exception;
use tests\unit\fixtures\UserFixture;

class RecordingCest
{
    private string $sessionId;
    private string $apiKey = 'secret-api-key-123';

    public function _before(ApiTester $I): void
    {
        $I->haveHttpHeader('X-Test-Mode', 'true');
    }

    public function _fixtures(): array
    {
        return [
            'users' => [
                'class'    => UserFixture::class,
                'dataFile' => '@app/tests/unit/fixtures/data/users.php',
            ],
        ];
    }


    public function tryUnauthorizedAccess(ApiTester $I): void
    {
        $I->wantTo('Check auth protection');
        $I->sendPOST('/api/recording/start', ['department' => 'sales', 'operator_name' => 'tester']);
        $I->seeResponseCodeIs(HttpCode::UNAUTHORIZED);
    }

    /**
     * @throws Exception
     */
    public function tryStartRecording(ApiTester $I): void
    {
        $I->wantTo('Start recording with valid key');

        $I->haveHttpHeader('X-Api-Key', $this->apiKey);
        $I->sendPOST('/api/recording/start', [
            'department'    => 'sales',
            'operator_name' => 'tester_integ'
        ]);

        $I->seeResponseCodeIs(HttpCode::OK);
        $I->seeResponseIsJson();
        $I->seeResponseContainsJson(['status' => 'success']);

        $this->sessionId = $I->grabDataFromResponseByJsonPath('$.session_id')[0];
    }

    public function tryValidationFail(ApiTester $I): void
    {
        $I->wantTo('Check validation errors');

        $I->haveHttpHeader('X-Api-Key', $this->apiKey);
        $I->sendPOST('/api/recording/start', []);

        $I->seeResponseContainsJson([
            'status'  => 'error',
            'message' => 'Ошибка валидации данных'
        ]);
    }

    public function tryStopNonExistentSession(ApiTester $I): void
    {
        $I->wantTo('Try to stop session that does not exist');
        $I->haveHttpHeader('X-Api-Key', $this->apiKey);

        $fakeUuid = '00000000-0000-0000-0000-000000000000';

        $I->sendPOST('/api/recording/stop', [
            'session_id'    => $fakeUuid,
            'department'    => 'sales',
            'operator_name' => 'tester',
            'audio_data'    => 'Zm9vYmFy'
        ]);

        $I->seeResponseCodeIs(HttpCode::INTERNAL_SERVER_ERROR);
        $I->seeResponseContainsJson(['status' => 'error']);
    }

    /**
     * @throws Exception
     */
    public function tryFullRecordingCycle(ApiTester $I): void
    {
        $I->wantTo('Run full cycle: Start -> Stop');
        $I->haveHttpHeader('X-Api-Key', $this->apiKey);
        $I->sendPOST('/api/recording/start', [
            'department'    => 'support',
            'operator_name' => 'agent007'
        ]);

        $I->seeResponseCodeIs(HttpCode::OK);
        $sessionId = $I->grabDataFromResponseByJsonPath('$.session_id')[0];

        $dummyAudio = 'data:audio/webm;base64,';

        $I->sendPOST('/api/recording/stop', [
            'session_id'    => $sessionId,
            'department'    => 'support',
            'operator_name' => 'agent007',
            'audio_data'    => $dummyAudio
        ]);

        $I->seeResponseCodeIs(HttpCode::OK);
        $I->seeResponseContainsJson([
            'status'  => 'success',
            'message' => 'Запись сохранена'
        ]);

        $I->seeResponseJsonMatchesJsonPath('$.file_path');
        $I->seeResponseJsonMatchesJsonPath('$.duration');
    }
}